 import React, { Component } from 'react'
 import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {fetchMeals} from '../actions/mealActions'
import '../App.css'




class Meals extends Component {
    componentWillMount(){
        this.props.fetchMeals();
    }


    render() {
        const mealItems=this.props.meals.map(meal=> (
            <div key = {meal.id}>


    <h3>{meal.name}</h3>


     <img className="image-styling" 
style={{ backgroundImage: `linear-gradient(rgba(0,0,0,0.3), rgba(0,0,0,0.2)), url('${meal.imageUrl}')` }}/>
<img src= {`url('${meal.imageUrl}')`}/>

        <p>{meal.price}</p>
       <p>{meal.priceCurrency}</p>

        </div>
        )); 

    return (
            <div>
            <h1>Popular meals</h1>
            {mealItems}


            </div>
        );
    }
}


Meals.propTypes = {
fetchMeals:PropTypes.func.isRequired,
meals:PropTypes.array.isRequired
}

const mapStateToProps = state => ({
   meals:state.meals.items
});

export default connect(mapStateToProps,{fetchMeals})(Meals);
